mapper.select:
select
concat(year(create_time), '-',
if(month(create_time) < 10, concat('0', month(create_time)), month(create_time)), '-',
if(day(create_time) < 10, concat('0', day(create_time)), day(create_time))) as date,
count(*) as count
from
question_answer

    and del_flag = 0
    and user_id = #{params.userId}
    and create_time < #{params.endDate}

group by
year(create_time), month(create_time), day(create_time)
